mapper.update:
update author
set author_id = #{authorId,jdbcType=VARCHAR},
  author_name = #{authorName,jdbcType=VARCHAR},
  author_desc = #{authorDesc,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, author_id, author_name, author_desc
from author
where id = #{id,jdbcType=BIGINT}
